import React from "react";

function onMouseEnter() {
  document.getElementById("movie-detail").classList.add("hide");
}
function onMouseLeave() {
  document.getElementById("movie-detail").classList.remove("hide");
}

export default function Detail({ detail, title }) {
  return (
    <div className="movie-detail">
      <div>
        <h5>{title}</h5>
        <p>{detail}</p>
      </div>
    </div>
  );
}
