import React from "react";

const IMG_BASE_URL = "https://image.tmdb.org/t/p/w1280/";

export default function Movie({ title, poster_path, vote_average }) {
  function handleEnter() {
    document.getElementById("dt").classList.remove("hide");
    document.getElementById("mc").classList.add("opacity");
  }
  function handleLeave() {
    document.getElementById("dt").classList.add("hide");
    document.getElementById("mc").classList.remove("opacity");
  }
  return (
    <div
      id="mc"
      className="movie-container"
      onMouseEnter={handleEnter}
      onMouseLeave={handleLeave}
    >
      <img src={IMG_BASE_URL + poster_path} alt="영화 포스터" />
      <div className="movie-info">
        <h4>{title}</h4>
        <span>{vote_average}</span>
      </div>
    </div>
  );
}
